import React from 'react';
import Tile from './Tile.js';
import Menu from './Menu.js';

class Tourney extends React.Component {
  constructor(props) {
    super(props);
    this.id = atob(props.id);
    this.createRow = this.createRow.bind(this);
    this.createMap = this.createMap.bind(this);
    this.updateMap = this.updateMap.bind(this);
    this.updateTile = this.updateTile.bind(this);
    this.makeTile = this.makeTile.bind(this);
    this.handleDrop = this.handleDrop.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.row = 0;
    this.col = 0;
    this.state = {
      map: [],
      display: [],
      menu: ["", "", "", ""]
    };

    this.createMap();

    props.socket.emit('get tourney', this.id);

    props.socket.on('load tourney', function(data) {
      if (this.mounted) {
        console.log(data);
        this.updateMap(data.map);
      }
    }.bind(this));
  }

  componentDidMount() {
    this.mounted = true;
  }

  componentWillUnmount() {
    this.mounted = false;
  }

  createMap() {
    let map = [];
    for (let i = 0; i < 10; i++) {
      map.push(this.createRow(i));
    }
    this.setState({display: map})
    return map;
  }

  createRow(n) {
    let row = [];
    for (let i = 0; i < 18; i++) {
      row.push(<Tile row={n} col={i} click={this.handleClick} drop={this.handleDrop} key={i + 100 * n} />);
    }
    let out = []
    out.push(<div className="grid-row" key={1000 * n}>{row}</div>);
    return out;
  }

  updateMap(newMap) {
    let copy = this.state.display.slice();
    for (let i = 0; i < 10; i++) {
      for (let j = 0; j < 18; j++) {
        let type = newMap[i][j][0];
        let title = newMap[i][j][1];
        let info = newMap[i][j][2];
        let highlight = newMap[i][j][3];
        copy[i][j] = this.makeTile(i, j, type, title, info, highlight);
      }
    }
    this.setState({map: newMap});
  }

  updateTile(row, col, type, title, info, highlight) {
    let copy = this.state.map.slice()
    this.map[row][col] = this.makeTile(row, col, type, title, info, highlight);
    copy[row][col] = [type, title, info, highlight];
    this.setState({map: copy});
  }

  handleDrop(e) {
    e.preventDefault();
    let r1 = parseInt(e.dataTransfer.getData("row"));
    let c1 = parseInt(e.dataTransfer.getData("col"));
    let r2 = e.target.getAttribute("data-row");
    let c2 = e.target.getAttribute("data-col");
    let temp = this.state.map[r1][c1];
    let copy = this.state.map.slice();
    copy[r1][c1] = copy[r2][c2];
    copy[r2][c2] = temp;
    this.updateMap(copy);
    console.log(copy);
    this.setState({map: copy});
    console.log("switched!");
  }

  handleClick(e) {
    console.log("clicked!");
    console.log(e.target);
    let row = e.target.getAttribute("data-row");
    let col = e.target.getAttribute("data-col");
    let vals = this.state.map[row][col];
    this.setState({menu: vals});
    this.row = row;
    this.col = col;
  }

  makeTile(row, col, type, title, info, highlight) {
    return (
      <Tile row={row} col={col} type={type} title={title} click={this.handleClick}
        info={info} highlight={highlight} drop={this.handleDrop} />
    )
  }

  render() {
    console.log(this.map[0][0]);
    return (
      <div className="tourney">
        <Menu name={this.id} type={this.state.menu[0]}
          title={this.state.menu[1]} info={this.state.menu[1]} highlight={this.state.menu[2]}
          row={this.row} col={this.col} updateTile={this.updateTile} />
        <div className="tourney-display">
          <div className="tourney-grid">{this.map}</div>
        </div>
      </div>
    )
  }
}

export default Tourney;
