import React from 'react';

class Menu extends React.Component {
  constructor(props) {
    super(props);
    this.name = props.name;
    this.row = props.row;
    this.col = props.col;
    this.updateTile = props.updateTile;

    if (props.type == "") {
      this.type = "blank";
    }

    this.state = {
      type: this.type,
      title: props.title,
      info: props.info,
      highlight: props.highlight
    };

    this.typeChange = this.typeChange.bind(this);
    this.titleChange = this.titleChange.bind(this);
  }

  typeChange(e) {
    //this.setState({type: e.target.value});
  }

  titleChange(e) {
    this.updateTile(this.row, this.col, this.state.type, e.target.value,
      this.state.info, this.state.highlight);
    console.log(e.target.value);
    this.setState({title: e.target.value});
  }

  render() {
    return (
      <div className="tourney-menu">
        <div>{this.name}</div>
        <select value={this.state.type} onChange={this.typeChange}>
          <option value="blank">Blank</option>
          <option value="text">Text</option>
          <option value="timer">Timer</option>
        </select>
        <input type="text" value={this.state.title} onChange={this.titleChange}/>
        <div>{this.title}</div>
        <div>{this.info}</div>
        <div>{this.highlight}</div>
      </div>
    )
  }
}

export default Menu;
