import React from 'react';
import Tile from './Tile.js';

class Row extends React.Component {
  constructor(props) {
    super(props);
    this.createRow = this.createRow.bind(this);
  }

  createRow() {
    let tiles = []
    let row = this.props.row;
    let map = this.props.map
    for (let i = 0; i < 18; i++) {
      tiles.push(<Tile key={i} row={row} col={i} type={map[row][i][0]}
        title={map[row][i][1]} info={map[row][i][2]} highlight={map[row][i][3]}
          click={this.props.handleClick} drop={this.props.handleDrop} />);
    }
    let rows = []
    rows.push(<div key={row} className="grid-row">{tiles}</div>);
    return rows;
  }

  render() {
    return (
      <div className="grid-row">
        {this.createRow()}
      </div>
    )
  }
}

export default Row;
