import React from 'react';

class Tourney extends React.Component {
  constructor(props) {
    super(props);
    this.id = atob(props.id);
    this.createMap = this.createMap.bind(this.createMap);
    this.state = {
      map: []
    };

    props.socket.emit('get tourney', this.id);

    props.socket.on('update tourney', function(data) {
      if (this.mounted) {
        console.log(data);
        this.setState({map: data.map});
      }
    }.bind(this));
  }

    componentDidMount() {
      this.mounted = true;
    }

    componentWillUnmount() {
      this.mounted = false;
    }

    createMap() {
      let map = [];
      for (let i = 0; i < 10; i++) {
        let tiles = [];
        for (let j = 0; j < 20; j++) {
          tiles.push(<div className="grid-tile"></div>);
        }
        map.push(<div className="grid-row">{tiles}</div>);
      }
      return map;
    }

  render() {
    return (
      <div>
        <p>{this.props.id}</p>
        <div className="grid-row">

        </div>

      </div>
    )
  }
}

export default Tourney;
