import React from 'react';

class Menu extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      type: this.type,
      title: props.title,
      info: props.info,
      highlight: props.highlight
    };

    this.typeChange = this.typeChange.bind(this);
    this.tileChange = this.tileChange.bind(this);
    this.getInfo = this.getInfo.bind(this);
  }

  typeChange(e) {
    this.setState({type: e.target.value});
  }

  tileChange(e) {
    this.props.updateTile(this.props.row, this.props.col, this.state.type, e.target.value,
      this.state.info, this.state.highlight);
  }

  getInfo() {
    if (this.props.type === "text") {
      console.log("yesss");
      return (
        <div>
          <p className="menu-text">Title:</p>
          <input type="text" value={this.props.info} onChange={this.tileChange}/>
        </div>
      )
    }
  }

  render() {
    return (
      <div className="tourney-menu">
        <div className="tourney-name">{this.props.name}</div>
        <select value={this.props.type} onChange={this.typeChange}>
          <option value="">Blank</option>
          <option value="text">Text</option>
          <option value="timer">Timer</option>
        </select>
        <div>
          <p className="menu-text">Title:</p>
          <input type="text" value={this.props.title} onChange={this.tileChange}/>
        </div>
        <div>{this.getInfo()}</div>
        <div>{this.highlight}</div>
      </div>
    )
  }
}

export default Menu;
