import React from 'react';
import Tile from './Tile.js';

class Tourney extends React.Component {
  constructor(props) {
    super(props);
    this.id = atob(props.id);
    this.createRow = this.createRow.bind(this);
    this.createMap = this.createMap.bind(this);
    this.renderTile = this.renderTile.bind(this);
    this.state = {
      map: []
    };

    props.socket.emit('get tourney', this.id);

    props.socket.on('update tourney', function(data) {
      if (this.mounted) {
        console.log(data);
        this.setState({map: data.map});
      }
    }.bind(this));
  }

  componentDidMount() {
    this.mounted = true;
  }

  componentWillUnmount() {
    this.mounted = false;
  }

  createMap() {
    let map = [];
    for (let i = 0; i < 10; i++) {
      map.push(this.createRow(i));
    }
    return map;
  }

  createRow(n) {
    let row = [];
    for (let i = 0; i < 20; i++) {
      row.push(<Tile key={i + 100 * n} />);
    }
    let out = []
    out.push(<span className="grid-row">{row}</span>);
    return out;
  }

  renderTile(n) {
    return (
      <div>
        <Tile key={n} />
      </div>
    )
  }

  render() {
    return (
      <div>
        <div>{this.props.id}</div>
        {this.createMap()}
      </div>
    )
  }
}

export default Tourney;
