import React from 'react';

class View extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: props.id,
      map: []
    };

    props.socket.emit('get view', props.id);

    props.socket.on('update view', function(data) {
      if (this.mounted) {
        console.log(data);
        this.setState({map: data.map});
      }
    }.bind(this));
  }

    componentDidMount() {
      this.mounted = true;
    }

    componentWillUnmount() {
      this.mounted = false;
    }

  render() {
    return (
      <div>
        <p>{this.props.id}</p>
        <p>{this.state.status}</p>
        <p>{this.state.queue}</p>
        <p>{this.state.matches}</p>
      </div>
    )
  }
}

export default View;
