import React from 'react';

class Tile extends React.Component {
  constructor(props) {
    super(props);
    this.row = props.row;
    this.col = props.col;
    this.type = props.type;
    this.handleClick = props.click;
    this.handleDrop = props.drop;
    this.handleDrag = this.handleDrag.bind(this);
    this.handleDragOver = this.handleDragOver.bind(this);

    this.state = {
      title: props.title,
      info: props.info,
      highlight: props.highlight
    };
  }

  handleDrag(e) {
    e.dataTransfer.setData("row", e.target.getAttribute("data-row"));
    e.dataTransfer.setData("col", e.target.getAttribute("data-col"));
  }

  handleDragOver(e) {
    e.preventDefault();
  }

  render() {
    console.log("doin");
    return (
      <div onClick={this.handleClick} data-row={this.row} data-col={this.col} draggable="true" onDragOver={this.handleDragOver}
        onDragStart={this.handleDrag} onDrop={this.handleDrop} className="grid-tile">{this.state.title}</div>
    )
  }
}

export default Tile
