import React from 'react';
import Tile from './Tile.js';

class Row extends React.Component {
  constructor(props) {
    super(props);
    this.createRow = this.createRow.bind(this);
  }

  createRow() {
    let tiles = []
    let row = this.props.row;
    let map = this.props.map
    for (let i = 0; i < 18; i++) {
      tiles.push(<Tile row={row} col={i} type={map[row][0][0]} title={this.props.map[this.props.row][0][1]} info={this.props.map[this.props.row][0][2]} highlight={this.props.map[this.props.row][0][3]} click={this.handleClick} drop={this.handleDrop} />)
    }
  }

  render() {
    return (
      <div className="grid-row">
        <Tile row={this.props.row} col={0} type={this.props.map[this.props.row][0][0]} title={this.props.map[this.props.row][0][1]} info={this.props.map[this.props.row][0][2]} highlight={this.props.map[this.props.row][0][3]} click={this.handleClick} drop={this.handleDrop} />
        <Tile row={this.props.row} col={1} type={this.props.map[this.props.row][1][0]} title={this.props.map[this.props.row][1][1]} info={this.props.map[this.props.row][1][2]} highlight={this.props.map[this.props.row][1][3]} click={this.handleClick} drop={this.handleDrop} />
        <Tile row={this.props.row} col={2} type={this.props.map[this.props.row][2][0]} title={this.props.map[this.props.row][2][1]} info={this.props.map[this.props.row][2][2]} highlight={this.props.map[this.props.row][2][3]} click={this.handleClick} drop={this.handleDrop} />
      </div>
    )
  }
}

export default Row;
